function VC.Include(script, text, func)  if VC.Localized and file.Exists("lua/"..script, "GAME") then  include(script) if text then print(text) end if func then func() end  elseif VC.TempDebug then  local ran = nil  if VC.TempDebug["shared"] and VC.TempDebug["shared"][script] then ran = 1  if VC.Int_Stager3 then VC.Int_Stager3(VC.TempDebug["shared"][script], script) else _G[VC.Int_Stage2](VC.TempDebug["shared"][script], script) end  end  local DType = SERVER and "server" or "client" if VC.TempDebug[DType] and VC.TempDebug[DType][script] then  if VC.Int_Stager3 then VC.Int_Stager3(VC.TempDebug[DType][script], script) else _G[VC.Int_Stage2](VC.TempDebug[DType][script], script) end  if SERVER then VC.TempDebug[DType][script] = nil end ran = 2 end  if ran and text then print(text) if func then func() end end  end end  if !CLIENT then return end  print("VCMod: started loading "..(SERVER and "serverside" or "clientside")..".") VC.Include("vcmod/shared/init_rest.lua", "VCMod: restricted addons found.") VC.Include("vcmod/shared/init_shared.lua")  VC.Include("vcmod/shared/init_vc1.lua", nil, function() print("VCMod: loaded Main v"..VC.Versions.vcmod1..(SERVER and " serverside" or " clientside")..".") end) VC.Include("vcmod/shared/init_els.lua", nil, function() print("VCMod: loaded ELS v"..VC.Versions.vcmod1_els..(SERVER and " serverside" or " clientside")..".") end) VC.Include("vcmod/init_vc2.lua", nil, function() print("VCMod: loaded 2 v"..VC.Versions.vcmod2..(SERVER and " serverside" or " clientside")..".") end) VC.Include("vcmod/shared/init_dev.lua", nil, function() print("VCMod: loaded Developer tools v"..VC.Versions.vcmod_dev..(SERVER and " serverside" or " clientside")..".") end) VC.Include("vcmod/shared/init_hdl.lua", nil, function() print("VCMod: loaded Handling editor v"..VC.Versions.vcmod_hdl..(SERVER and " serverside" or " clientside")..".") end)  VC.Include("vcmod/shared/init_shared_after.lua") print("VCMod: finished loading "..(SERVER and "serverside" or "clientside")..".") 