if !VC.Menu_Items_P then VC.Menu_Items_P = {} end if !VC.Menu_Items_A then VC.Menu_Items_A = {} end  local function BuildMenu(Pnl)  local Font2 = "VC_Treb24_Small" if !VC.Fonts[Font2] then VC.Fonts[Font2] = true surface.CreateFont(Font2, {font = "Trebuchet24", size = 17, weight = 500, blursize = 0, scanlines = 0, antialias = true, underline = false, italic = false, strikeout = false, symbol = false, rotary = false, shadow = false, additive = false, outline = false}) end  local Font3 = "VC_Treb24_12" if !VC.Fonts[Font3] then VC.Fonts[Font3] = true surface.CreateFont(Font3, {font = "Trebuchet24", size = 12, weight = 500, blursize = 0, scanlines = 0, antialias = true, underline = false, italic = false, strikeout = false, symbol = false, rotary = false, shadow = false, additive = false, outline = false}) end  local Font_Logo = "VC_Logo" if !VC.Fonts[Font_Logo] then VC.Fonts[Font_Logo] = true surface.CreateFont(Font_Logo, {font = "MenuLarge", size = 40, weight = 1000, blursize = 2, scanlines = 0, antialias = true, underline = false, italic = false, strikeout = false, symbol = false, rotary = false, shadow = false, additive = false, outline = false}) end   local List = VC.Add_El_List(7, 130, Pnl:GetWide()-14, Pnl:GetTall()-82) List:SetParent(Pnl) List:SetSpacing(7)   Pnl.Paint = function(obj, Sx, Sy)  draw.DrawText(game.SinglePlayer() and VC.Lng("YouAreUsingVCMod") or VC.Lng("ServerIsUsingVCMod"), "VC_Big_Italic", List:GetWide()/2, 5, VC.Color.Neutral, TEXT_ALIGN_CENTER)  draw.DrawText(VC.Lng("Info_EverThought"), Font2, 50, 50, Color(255, 255, 255, 255), TEXT_ALIGN_LEFT)  draw.DrawText(VC.Lng("Info_VCModIsDesigned"), Font2, 10, 68, Color(255, 255, 255, 255), TEXT_ALIGN_LEFT)   draw.DrawText(VC.Lng("Info_VCModHasFollowingAddons"), Font2, 10, 100, Color(255, 255, 255, 255), TEXT_ALIGN_LEFT)  end   local news = vgui.Create("DHTML", Pnl) news:OpenURL(VC.Host.."api/news.php?st=ns") news:SetPos(5, Pnl:GetTall()*0.7) news:SetSize(Pnl:GetWide()-10, 152)  news.Paint = function(obj, Sx, Sy)  draw.RoundedBox(0, 0, 0, Sx, Sy, Color(255,255,255,255))  draw.DrawText(VC.Lng("WhyISellVCMod"), Font3, Sx+5, 8, VC.Color.Blue, TEXT_ALIGN_RIGHT)  end   local objects = {} if VC.DataServer then for k,v in pairs(VC.DataServer) do local El = vgui.Create("VC_Ball", Pnl) El:SetSize(v.size,v.size) El:SetPos(Pnl:GetWide()*v.pos_x-v.size/2, Pnl:GetTall()*v.pos_y-v.size/2) local Px, Py = El:GetPos() objects[k] = {El, Px, Py, math.Rand(0.5,3), math.Rand(0.5,3), math.Rand(2,4), math.Rand(2,4)} El:Setup(v) El:NoClipping(true) end end   Pnl.Think = function()  for k,v in pairs(objects) do  local Px, Py = v[2], v[3] local sin = math.sin(CurTime()*v[4])*v[6]  v[1]:SetPos(Px+math.sin(CurTime()*v[4])*v[6], Py+math.sin(CurTime()*v[5])*v[7])  end  end   local Btn = vgui.Create("DButton") Btn:SetPos(5, Pnl:GetTall()-185) Btn:SetSize(145, 25) Btn:SetText(" ") Btn:SetParent(Pnl) Btn:NoClipping(true)  Btn.DoClick = function() gui.OpenURL(VC.Host.."api/whyisellvcmod.php?st=ws") end  Btn.Paint = function(stuff, Sx, Sy)  if Btn:IsHovered() then  local clr = table.Copy(VC.Color.Main)  draw.RoundedBox(0, 0, 0, Sx+12, Sy, clr)  end  draw.DrawText(VC.Lng("WhyISellVCMod"), Font3, Sx/2+5, 8, VC.Color.Blue, TEXT_ALIGN_CENTER)  end   local Btn = vgui.Create("DButton") Btn:SetPos(Pnl:GetWide()-130, Pnl:GetTall()-185) Btn:SetSize(125, 25) Btn:SetText(" ") Btn:SetParent(Pnl) Btn:NoClipping(true)  Btn.DoClick = function() gui.OpenURL("http://steamcommunity.com/id/freemmaann/") end  Btn.Paint = function(stuff, Sx, Sy)  if Btn:IsHovered() then  local clr = table.Copy(VC.Color.Main)  draw.RoundedBox(0, 0, 0, Sx+12, Sy, clr)  draw.RoundedBox(0, 137, 0, 135, 40, Color(clr.r,clr.g,clr.b,248))   draw.DrawText("skype:", Font3, Sx+15, 8, VC.Color.Blue, TEXT_ALIGN_LEFT)  draw.DrawText("comman6 ", Font3, Sx+50, 8, VC.Color.Good, TEXT_ALIGN_LEFT)    draw.DrawText("email:", Font3, Sx+15, 20, VC.Color.Blue, TEXT_ALIGN_LEFT)  draw.DrawText("freemmaann@gmail ", Font3, Sx+50, 20, VC.Color.Good, TEXT_ALIGN_LEFT)  end  draw.DrawText(VC.Lng("CreatedBy")..": ", Font3, Sx-60, 8, VC.Color.Blue, TEXT_ALIGN_RIGHT)  draw.DrawText("freemmaann", Font3, Sx-5, 8, VC.Color.Good, TEXT_ALIGN_RIGHT)  end end VC.Menu_Info_Panel_Build = {"Info", BuildMenu}  local function BuildMenu(Pnl)  local PW = Pnl:GetWide()  local List = VC.Add_El_List(5, 40, PW-5, Pnl:GetTall()) List:SetParent(Pnl)  local html = vgui.Create("DHTML", List) html:OpenURL(VC.Host.."ingame/changelog_main.php") html:SetPos(0, 0) html:SetSize(List:GetSize()) html.Paint = function(obj, Sx, Sy) surface.SetDrawColor(255, 255, 255, 255) surface.SetMaterial(Material("vcmod/bg.png")) surface.DrawTexturedRect(0,0,Sx,Sy) end   local X, Y = List:GetPos() local Sx, Sy = List:GetSize()  Pnl.Paint = function(obj, Sx, Sy)  draw.DrawText(VC.Lng("UpdatesAreAppliedAutomaticallyAfterMapRestart"), "VC_Dev_Text", List:GetWide()/2, 10, VC.Color.White, TEXT_ALIGN_CENTER)  end  return Draw end VC.Menu_Update_Panel_Build = {"Updates", BuildMenu} local rs7 = "vc.check_msg_work_ALS_k4" 